import { useNavigation } from "@react-navigation/native";
import axios from "axios";
import Constants from "expo-constants";
import React, { useEffect, useState } from "react";
import {
  Dimensions,
  Image,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
} from "react-native";
import { TouchableWithoutFeedback } from "react-native-gesture-handler";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import { connect } from "react-redux";
import MovieItems from "../components/MovieItems";
import {
  addToWishList,
  fetchMovies,
  fetchSeries,
  fetchTopRatedMovies,
  fetchUpcomingMovies,
  removeFromWishlist,
} from "../redux/actions";

const _HomeScreen = (props) => {
  const navigation = useNavigation();
  const {
    movieReducer,
    fetchMovies,
    fetchSeries,
    addToWishList,
    removeFromWishlist,
    fetchTopRatedMovies,
    fetchUpcomingMovies,
  } = props;

  const { movies, series, wishlist, topMovies, upcomingMovies } = movieReducer;

  const [currentMovie, setCurrentMovie] = useState(undefined);
  const [listTopMovie, setListTopMovie] = useState([]);
  const [listUpcomingMovie, setListUpcomingMovie] = useState();
  const [listSerie, setListSerie] = useState([]);
  const [query, setQuery] = useState("");
  const [queryResult, setQueryResult] = useState([]);

  useEffect(() => {
    fetchTopRatedMovies();
    setListTopMovie(topMovies);
  }, [topMovies]);

  useEffect(() => {
    fetchUpcomingMovies();
    setListUpcomingMovie(upcomingMovies);
  }, [upcomingMovies]);

  const searchData = (query) => {
    axios
      .get(
        "https://api.themoviedb.org/3/search/movie?api_key=afd804ef50f1e6b1ad6f29209e9395e6&language=fr-FR&query=" +
          query +
          "&page=1&include_adult=false"
      )
      .then((response) => {
        setQueryResult(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const goSearchResults = () => {
    navigation.navigate("ResultsQueryMovie", { queryResult: queryResult });
    setQuery("");
  };

  const onTapAddToWishlist = (movie) => {
    addToWishList(movie);
  };

  const onTapRemoveFromWishlist = (movie) => {
    removeFromWishlist(movie);
  };

  const isExist = (movie) => {
    if (wishlist.filter((item) => item._id === movie._id).length > 0) {
      return true;
    }

    return false;
  };

  return (
    <SafeAreaView
      style={styles.container}
      showHorizontalScrollIndicator={false}
    >
      <View style={styles.header}>
        <View style={{ flexDirection: "row" }}>
          <TextInput
            style={{
              backgroundColor: "white",
              width: 200,
              borderRadius: 10,
              marginTop: 8,
            }}
            data={queryResult}
            placeholder="Rechercher un film"
            onChangeText={(query) => {
              searchData(query);
            }}
          />
          <TouchableWithoutFeedback
            style={{
              width: 24,
              height: 40,
              justifyContent: "flex-end",
              marginLeft: 15,
            }}
            onPress={goSearchResults}
          >
            <MaterialCommunityIcons
              name="magnify"
              color={"#998CF8"}
              size={30}
            />
          </TouchableWithoutFeedback>
        </View>
      </View>

      <ScrollView>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text
            style={{
              marginBottom: 20,
              fontSize: 32,
              fontWeight: "bold",
              color: "#998CF8",
            }}
          >
            Films
          </Text>
          <Image
            style={styles.cover_image}
            resizeMode="cover"
            source={{
              uri:
                "https://image.tmdb.org/t/p/w500/hziiv14OpD73u9gAak4XDDfBKa2.jpg",
            }}
          />
        </View>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={styles.section}>Films populaires</Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              name="chevron-right"
              color={"#998CF8"}
              size={32}
            />
          </View>
        </View>
        <ScrollView horizontal={true}>
          <View
            style={{
              flexDirection: "row",
              flex: 1,
              paddingLeft: 20,
              paddingRight: 20,
            }}
          >
            {listTopMovie.map((movie, index) => {
              return index < 20 ? (
                <MovieItems
                  key={movie.id}
                  movie={movie}
                  title={movie.title}
                  image={movie.poster_path}
                />
              ) : (
                <View key={movie.id} />
              );
            })}
          </View>
        </ScrollView>

        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={styles.section}>Films r√©cent</Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons
              name="chevron-right"
              color={"#998CF8"}
              size={32}
            />
          </View>
        </View>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: "row", flex: 1, paddingLeft: 20 }}>
            {upcomingMovies.map((movie, index) => {
              return index < 20 ? (
                <MovieItems
                  key={movie.id}
                  movie={movie}
                  title={movie.title}
                  image={movie.poster_path}
                />
              ) : (
                <View key={movie.id} />
              );
            })}
          </View>
        </ScrollView>
      </ScrollView>
    </SafeAreaView>
  );
};

const mapStateToProps = (state) => ({
  movieReducer: state.movieReducer,
});

const HomeScreen = connect(mapStateToProps, {
  fetchMovies,
  addToWishList,
  removeFromWishlist,
  fetchTopRatedMovies,
  fetchUpcomingMovies,
  fetchSeries,
})(_HomeScreen);

export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: Constants.statusBarHeight,
    paddingVertical: 20,
    backgroundColor: "#111112",
  },
  header: {
    width: "100%",
    flexDirection: "row",
    justifyContent: "center",
    paddingHorizontal: 20,
    marginBottom: 10,
  },
  title: {
    fontWeight: "bold",
    fontSize: 24,
    flex: 1,
  },
  posterView: {
    display: "flex",
    width: Dimensions.get("screen").width,
    flex: 7,
    flexDirection: "column",
    justifyContent: "space-between",
  },
  listView: {
    width: Dimensions.get("screen").width,
    flex: 5,
    padding: 10,
  },
  poster: {
    display: "flex",
    width: Dimensions.get("screen").width,
    height: "100%",
    justifyContent: "flex-end",
    flexDirection: "column",
  },
  movieCard: {
    display: "flex",
    flexDirection: "column",
    width: Dimensions.get("screen").width / 2.5 - 10,
    backgroundColor: "#FFF",
    borderRadius: 20,
    height: "80%",
    justifyContent: "space-between",
    alignItems: "center",
    marginLeft: 10,
  },
  cover_image: {
    width: "100%",
    height: 256,
    borderRadius: 10,
    marginBottom: 10,
  },
  input: {
    height: 40,
    width: 155,
    margin: 12,
    borderWidth: 1,
  },
  autocompleteContainer: {
    flex: 1,
    left: 0,
    position: "absolute",
    right: 0,
    top: 0,
    zIndex: 1,
  },
  section: {
    color: "#fff",
    fontSize: 32,
    fontWeight: "bold",
  },
});
