import { useNavigation } from "@react-navigation/native";
import axios from "axios";
import Constants from "expo-constants";
import React, { useEffect, useState } from "react";
import {
  Button,
  Dimensions,
  Animated,
  Image,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  View,
} from "react-native";
import { TouchableWithoutFeedback } from "react-native-gesture-handler";
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";
import { connect } from "react-redux";
import SerieItems from "../components/SerieItems";
import {
  addToWishList,
  fetchOnAirSeries,
  fetchSeries,
  fetchTvSeries,
  fetchTopRatedMovies,
  fetchUpcomingMovies,
  removeFromWishlist,
} from "../redux/actions";

const _SerieScreen = (props) => {
  const navigation = useNavigation();
  const {
    movieReducer,
    fetchOnAirSeries,
    fetchSeries,
    fetchTvSeries,
    addToWishList,
    removeFromWishlist,
    fetchTopRatedMovies,
    fetchUpcomingMovies,
  } = props;

  const { onAirSeries, series, tvSeries, wishlist, topMovies, upcomingMovies } = movieReducer;

  const [currentMovie, setCurrentMovie] = useState(undefined);
  const [listTopMovie, setListTopMovie] = useState([]);
  const [listUpcomingMovie, setListUpcomingMovie] = useState();
  const [listSerie, setListSerie] = useState([]);
  const [listTvSerie, setListTvSerie] = useState([]);
  const [listAirSerie, setlistAirSerie] = useState([]);
  const [query, setQuery] = useState("");
  const [queryResult, setQueryResult] = useState([]);
  const [iconName, setIconName] = useState("magnify")
  const [isAnimating, setIsAnimating] = useState(false)
  const [fadeAnim, setFadeAnim] = useState(new Animated.Value(24))

  const  deviceWidth = Dimensions.get("window").width;


  useEffect(() => {
    fetchSeries();
    setListSerie(series);
  }, [series]);

  useEffect(() => {
    fetchOnAirSeries();
    setlistAirSerie(onAirSeries);
  }, [onAirSeries]);

  useEffect(() => {
    fetchTvSeries();
    setListTvSerie(tvSeries);
  }, [tvSeries]);


  const searchData = (query) => {
    axios
      .get(
        "https://api.themoviedb.org/3/search/tv?api_key=afd804ef50f1e6b1ad6f29209e9395e6&language=fr-FR&query=" +
          query +
          "&page=1&include_adult=false"
      )
      .then((response) => {
        setQueryResult(response.data.results);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  const resetQuery = () => {
    setQuery("")
  }

  const goSearchResults = () => {
    navigation.navigate("ResultsQuerySerie", { queryResult: queryResult, resetQuery:resetQuery });

  };

  const onTapAddToWishlist = (serie) => {
    addToWishList(serie);
  };

  const onTapRemoveFromWishlist = (serie) => {
    removeFromWishlist(serie);s
  };

  const isExist = (serie) => {
    if (wishlist.filter((item) => item._id === serie._id).length > 0) {
      return true;
    }

    return false;
  };




  return (
    <SafeAreaView
      style={styles.container}
      showHorizontalScrollIndicator={false}
    >
      <View style={styles.header}>

        <View style={{ flexDirection: "row"}}>
          <TextInput
           style={{
            backgroundColor: "white",
            width: 200,
            borderRadius: 10,
            marginTop: 8
          }}
            data={query}
            placeholder="Rechercher une sÃ©rie"
            onChangeText={(query) => {
              searchData(query);
            }}
          />

        <TouchableWithoutFeedback
          style={{
            width: 24,
            height: 40,
            justifyContent: "flex-end",
            marginLeft: 15
          }}
          onPress={goSearchResults}
        >
          <MaterialCommunityIcons
            name="magnify"
            color={"#998CF8"}
            size={30}
          />
        </TouchableWithoutFeedback>


        </View>
      </View>

      <ScrollView>
        <View
          style={{
            flexDirection: "column",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={{marginBottom: 20, fontSize: 32, fontWeight: "bold", color:"#998CF8"}}>
            Series
          </Text>
          <Image
            style={styles.cover_image}
            source={{
              uri:
                "https://image.tmdb.org/t/p/w500/tsRy63Mu5cu8etL1X7ZLyf7UP1M.jpg",
            }}
          />
        </View>

        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={styles.section}>Popular series</Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons name="chevron-right" color={"#998CF8"} size={32} />
          </View>
        </View>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: "row", flex: 1, paddingLeft: 20 }}>
            {listSerie.map((serie, index) => {
              return index < 15 ? (
                <SerieItems
                  key={serie.id}
                  serie={serie}
                  title={serie.title}
                  image={serie.poster_path}
                />
              ) : (
                <View key={serie.id} />
              );
            })}
          </View>
        </ScrollView>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={styles.section}>On display </Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons name="chevron-right" color={"#998CF8"} size={32} />
          </View>
        </View>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: "row", flex: 1, paddingLeft: 20 }}>
            {listAirSerie.map((serie, index) => {
              return index < 20 ? (
                <SerieItems
                  key={serie.id}
                  serie={serie}
                  title={serie.title}
                  image={serie.poster_path}
                />
              ) : (
                <View key={serie.id} />
              );
            })}
          </View>
        </ScrollView>
        <View
          style={{
            flexDirection: "row",
            justifyContent: "space-between",
            alignItems: "center",
            paddingHorizontal: 20,
            marginVertical: 20,
          }}
        >
          <Text style={styles.section}>Top rated </Text>
          <View
            style={{
              flexDirection: "row",
              flexWrap: "wrap",
              alignItems: "center",
            }}
          >
            <MaterialCommunityIcons name="chevron-right" color={"#998CF8"} size={32} />
          </View>
        </View>
        <ScrollView horizontal={true}>
          <View style={{ flexDirection: "row", flex: 1, paddingLeft: 20 }}>
            {listTvSerie.map((serie, index) => {
              return index < 20 ? (
                <SerieItems
                  key={serie.id}
                  serie={serie}
                  title={serie.title}
                  image={serie.poster_path}
                />
              ) : (
                <View key={serie.id} />
              );
            })}
          </View>
        </ScrollView>
      </ScrollView>
    </SafeAreaView>
  );
};

const mapStateToProps = (state) => ({
  movieReducer: state.movieReducer,
});

const SerieScreen = connect(mapStateToProps, {
  fetchOnAirSeries,
  addToWishList,
  removeFromWishlist,
  fetchTopRatedMovies,
  fetchUpcomingMovies,
  fetchSeries,
  fetchTvSeries
})(_SerieScreen);

export default SerieScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    marginTop: Constants.statusBarHeight,
    paddingVertical: 20,
    backgroundColor: "#111112"
